function replace(value, del, find)
	del = del:gsub("[%(%)%.%+%-%*%?%[%]%^%$%%]", "%%%1");
	find = find:gsub("[%%]", "%%%%");
	return string.gsub(value, del, find);
end;
local StringData = function(str)
	local BadChars = {
		"%",
		"$"
	};
	local rstr = "";
	for k in string.gmatch(str, ".") do
		for x, y in pairs(BadChars) do
			if y == k then
				if k == "%" then
					k = "٪";
				elseif k == "$" then
					k = "&&";
				end;
				break;
			end;
		end;
		rstr = rstr .. k;
	end;
	return rstr;
end;
